$(function(){var $inputText=$('.query-input input, .query-input textarea');$inputText.each(function(){if(!$(this).val()){$(this).parent().find('label').show()}else{$(this).parent().find('label').hide()}});$inputText.focus(function(){if(!$(this).val()){$(this).parent().find('label').css({'opacity':'0.4'})}});$inputText.keydown(function(){if(!$(this).val()){$(this).parent().find('label').hide()}});$inputText.blur(function(){if(!$(this).val()){$(this).parent().find('label').show().css({'opacity':'1'})}});$('#city-select').selectBoxIt({defaultText:"Choose your city"});$('#city-select-2').selectBoxIt({defaultText:"Choose a city"});$(".nano").nanoScroller()});$(window).load(function(){$('.flexslider').flexslider({animation:"slide",controlNav:"thumbnails",start:function(slider){$('body').removeClass('loading')}})});$(window).resize(function(){$('.flexslider').flexslider({animation:"slide",controlNav:"thumbnails",start:function(slider){$('body').removeClass('loading')}})});